package java_for_dummies;

public class Casting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double pi = 3.1314;
		int ipi;
		ipi = (int) pi;
		System.out.println(ipi);
		
		double price = 9.99;
		int iPrice = (int) price;
		
		System.out.println(iPrice);

	}

}
